"use client";
import React, { useState } from 'react';
import Homebar from "../../Components/homebar";
// import Link from 'next/link';
import signIn from "../../Components/Authentication/signin.js";
import { useNavigate, Link } from 'react-router-dom';
import '../../App.css'

export default function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  const router = useNavigate();

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const { result, error } = await signIn(email, password);

      if (error) {
        console.error("Error signing in:", error);
        setError(error.message);
      } else {
        // Handle successful sign in
        if (result) {
          console.log("User signed in successfully:", result.user);
          // Notify the user and navigate to the home page
          alert("Sign in successful! Navigating to the home page...");
          router('/');
        } else {
          console.error("Error signing in:", error);
        //   setError("An error occurred during sign in");
        }
      }
    } catch (error) {
      console.error("Error signing in:", error);
    //   setError("An error occurred during sign in");
    }
  };

  return (
    <div className="signin-container">
      <Homebar />
      <div className="heading">
        <section className="self-stretch flex flex-col items-center justify-start gap-8 max-w-full text-left text-sm-8 text-color-02 font-poppins">
          <h2 className="text-2xl font-semibold mb-4">Login</h2>
          <form className="w-full max-w-sm flex flex-col gap-4" onSubmit={handleSubmit}>
            <div className="mb-4">
              <label htmlFor="email" className="block mb-1">
                Email
              </label>
              <input
                type="email"
                id="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                className="w-full px-2 py-2 border rounded-sm"
                style={{ marginBottom: '-10px' }}
              />
            </div>
            <div className="mb-4">
              <label htmlFor="password" className="block mb-1">
                Password
              </label>
              <input
                type="password"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="w-full px-2 py-2 border rounded-sm"
                style={{ marginBottom: '7px' }}
              />
              {error && <p className="text-red-500">{error}</p>}
            </div>
            <button
              type="submit"
              className="w-full py-2 bg-blue-500 text-white rounded-sm hover:bg-blue-600"
              style={{ marginLeft: '6px', marginRight: 'auto', display: 'block' }}
            >
              Login
            </button>
            <p style={{ textAlign: 'center' }}>
              Don't have an account? {' '}
              <Link to="./signup">Click here</Link> {' '}
              to create one!
            </p>
          </form>
        </section>
      </div>
    </div>
  );
}
