import React, { useState, useEffect } from 'react';
// import { auth } from '../../Components/Authentication/firebase-config';
// import { onAuthStateChanged } from "firebase/auth";
import { useAuth } from '../../Components/Authentication/AuthContext';
import './page.css';

const Profile = () => {
  const [user, setUser] = useState(null);
  const [gamesPlayed, setGamesPlayed] = useState(0);
  const [highScore, setHighScore] = useState(0);
  const [timesOpened, setTimesOpened] = useState(0);
  const { currentUser } = useAuth();

  useEffect(() => {
    const fetchUserData = async () => {
      const userData = {
        email: 'user@example.com',
        createdAt: new Date('2024-01-01'),
        activeTime: 120,
        games: [
          { name: 'Game 1', played: 10, highScore: 200, timesOpened: 5 },
        ],
      };

      setUser(userData);
      const totalGamesPlayed = userData.games.reduce((total, game) => total + game.played, 0);
      const highestScore = Math.max(...userData.games.map(game => game.highScore));
      const totalTimesOpened = userData.games.reduce((total, game) => total + game.timesOpened, 0);

      setGamesPlayed(totalGamesPlayed);
      setHighScore(highestScore);
      setTimesOpened(totalTimesOpened);
    };

    fetchUserData();
  }, []);

  const handleLogout = () => {
    console.log('User logged out');
  };

  const timeSinceCreation = user ? Math.floor((new Date() - user.createdAt) / (1000 * 60 * 60 * 24)) : 0;

  return (
    <div className="profile-container">
      {user ? (
        <>
          <div className="profile-header">
            <h1>Welcome, {user.email}</h1>
            <button onClick={handleLogout}>Logout</button>
          </div>
          <div className="profile-info">
            <p>Email: {user.email}</p>
            <p>Time since account creation: {timeSinceCreation} days</p>
            <p>Amount of time active: {user.activeTime} minutes</p>
          </div>
          <div className="profile-stats">
            <h2>Game Stats</h2>
            <table>
              <thead>
                <tr>
                  <th>Game</th>
                  <th>Games Played</th>
                  <th>High Score</th>
                  <th>Times Opened</th>
                </tr>
              </thead>
              <tbody>
                {user.games.map((game, index) => (
                  <tr key={index}>
                    <td>{game.name}</td>
                    <td>{game.played}</td>
                    <td>{game.highScore}</td>
                    <td>{game.timesOpened}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default Profile;
