"use client";
import React, { useState } from 'react';
import Homebar from "../../Components/homebar";
import signUp from "../../Components/Authentication/signup";
import { useNavigate, Link } from 'react-router-dom';
// import { Link } from 'react-router-dom';
import './page.css'
import '../../App.css'

export default function SignUpPage() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (password !== confirmPassword) {
        console.error("Passwords do not match");
    //   setError("Passwords do not match");
      return;
    }

    try {
      const { result, error } = await signUp(username, password);

      if (error) {
        console.error("Error signing up:", error);
        setError(error.message);
      } else {
        // Handle successful sign up
        if (result) {
          console.log("User signed up successfully:", result.user);
          // Notify the user and navigate to the home page
          alert("Sign up successful! Navigating to the home page...");
          navigate('/');
        } else {
          console.error("Error signing up:", error);
        //   setError("An error occurred during sign up");
        }
      }
    } catch (error) {
      console.error("Error signing up:", error);
    //   setError("An error occurred during sign up");
    }
  };

  return (
    <div className="signup-container">
      <div className="w-full relative bg-neutral-primary-01 flex flex-col items-center justify-start pt-16 px-4 pb-16 box-border min-h-screen flex-grow">
        <section className="self-stretch flex flex-col items-center justify-start gap-8 max-w-full text-left text-sm-8 text-color-02 font-poppins">
          <h2 className="text-2xl font-semibold mb-4">Sign Up</h2>
          <form className="w-full max-w-sm flex flex-col gap-4" onSubmit={handleSubmit}>
            <div className="mb-4">
              <label htmlFor="username" className="block mb-1">
                Username
              </label>
              <input
                type="text"
                id="username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                className="w-full px-3 py-2 border rounded-sm"
                style={{ marginBottom: '-10px' }}
              />
            </div>
            <div className="mb-4">
              <label htmlFor="password" className="block mb-1">
                Password
              </label>
              <input
                type="password"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="w-full px-3 py-2 border rounded-sm"
                style={{ marginBottom: '7px' }}
              />
            </div>
            <div className="mb-4">
              <label htmlFor="confirmPassword" className="block mb-1">
                Confirm Password
              </label>
              <input
                type="password"
                id="confirmPassword"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
                className="w-full px-3 py-2 border rounded-sm"
                style={{ marginBottom: '7px' }}
              />
              {error && <p className="text-red-500">{error}</p>}
            </div>
            <button
              type="submit"
              className="w-full py-2 bg-blue-500 text-white rounded-sm hover:bg-blue-600"
              style={{ marginLeft: '6px', marginRight: 'auto', display: 'block' }}
            >
              Submit
            </button>
            <p style={{ textAlign: 'center' }}>
              Already have an account? {' '}
              <Link to="../login">Click here</Link> {' '}
              to sign in!
            </p>
          </form>
        </section>
      </div>
    </div>
  );
}
